import React, { useState, useRef } from 'react';
import { processLogoUpload } from '../utils/logoUtils';
import './LogoUpload.css';

/**
 * Logo上传组件
 * @param {object} props - 组件属性
 * @param {string} props.logo - 当前logo数据
 * @param {function} props.onLogoChange - logo变化回调
 * @param {object} props.translations - 翻译对象
 * @param {boolean} props.isMobile - 是否为移动设备
 */
const LogoUpload = ({ logo, onLogoChange, translations, isMobile }) => {
  const [isUploading, setIsUploading] = useState(false);
  const [error, setError] = useState('');
  const fileInputRef = useRef(null);

  /**
   * 处理文件选择
   * @param {Event} event - 文件选择事件
   */
  const handleFileSelect = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setIsUploading(true);
    setError('');

    try {
      const result = await processLogoUpload(file);
      
      if (result.success) {
        onLogoChange(result.data);
        // 显示成功提示
        setTimeout(() => {
          setError('');
        }, 2000);
      } else {
        setError(result.error);
      }
    } catch (err) {
      setError(translations.logoUploadError);
    } finally {
      setIsUploading(false);
      // 清空文件输入框
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  /**
   * 处理拖拽上传
   * @param {Event} event - 拖拽事件
   */
  const handleDrop = async (event) => {
    event.preventDefault();
    const files = event.dataTransfer.files;
    if (files.length > 0) {
      const file = files[0];
      // 模拟文件选择事件
      const mockEvent = { target: { files: [file] } };
      await handleFileSelect(mockEvent);
    }
  };

  /**
   * 处理拖拽进入
   * @param {Event} event - 拖拽事件
   */
  const handleDragOver = (event) => {
    event.preventDefault();
  };

  /**
   * 删除Logo
   */
  const handleDeleteLogo = () => {
    if (window.confirm(translations.logoDeleteConfirm)) {
      onLogoChange('');
      setError('');
    }
  };

  /**
   * 触发文件选择
   */
  const handleClick = () => {
    if (fileInputRef.current) {
      fileInputRef.current.click();
    }
  };

  return (
    <div className="logo-upload-container">
      <label className="logo-upload-label">{translations.uploadLogo}</label>
      
      <div 
        className={`logo-preview ${isUploading ? 'uploading' : ''} ${error ? 'error' : ''}`}
        onClick={handleClick}
        onDrop={handleDrop}
        onDragOver={handleDragOver}
        onDragEnter={handleDragOver}
      >
        {isUploading ? (
          <div className="logo-uploading">
            <div className="spinner"></div>
            <span>{translations.logoUploading}</span>
          </div>
        ) : logo ? (
          <>
            <img 
              src={logo} 
              alt="Company Logo" 
              className="logo-image"
              onError={() => setError(translations.logoUploadError)}
            />
            <button 
              className="logo-delete-btn"
              onClick={(e) => {
                e.stopPropagation();
                handleDeleteLogo();
              }}
              title={translations.logoDelete}
            >
              ×
            </button>
          </>
        ) : (
          <div className="logo-placeholder">
            <svg className="logo-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
            </svg>
            <span>{translations.logoPlaceholder}</span>
          </div>
        )}
      </div>

      {error && (
        <div className="logo-error">
          {error}
        </div>
      )}

      <input
        ref={fileInputRef}
        type="file"
        accept="image/png,image/jpeg,image/jpg,image/webp"
        onChange={handleFileSelect}
        style={{ display: 'none' }}
      />
    </div>
  );
};

export default LogoUpload;
